command:example.dev:code:
$params = @{
    Uri = "https://contoso.com/rest/api/content"
    GetParameters = @{
        expand = "space,version,body.storage,ancestors"
        limit  = 30
    }
    Credential = $cred
}
Invoke-ConfluenceMethod @params

$params = @{
    Uri = "https://contoso.com/rest/api/content/10001/child/attachment/110001"
    Method = "GET"
    Headers    = @{"Accept" = "text/plain"}
    OutFile = "c:\temp\confidentialData.txt"
    Credential = $cred
}
Invoke-ConfluenceMethod @params

$pageObject = [ConfluencePS.Page]@{
    Title = "My Title"
    Space = [ConfluencePS.Space]@{
        Key="ABC"
    }
}

# example 1
New-ConfluencePage -InputObject $pageObject
# example 2
$pageObject | New-ConfluencePage
