command:example.dev:code:
$parameter = @{
    URI = "$(Get-JiraConfigServer)/rest/api/latest/project"
    Method = "GET"
    Headers = @{"Accept" = "text/plain"}
    OutFile = "c:\temp\jira_projects.json"
    Credential = $cred
}
Invoke-JiraMethod @parameter

$parameters = @{
    Project = "TEST"
    IssueType = "Bug"
    Priority = 1
    Summary = 'Test issue from PowerShell'
    Description = 'This is a test issue created from the JiraPS module in PowerShell.'
    Fields = @{
        "Custom Field Name 1" = @{"foo" = "bar"}
        customfield_10001 = @('baz')
    }
}
New-JiraIssue @parameters

$parameters = @{
    labels = @("DEPRECATED")
    AddComment = "Updated with a script"
    Fields = @{
        customfield_10000 = @(
            @{
                "value" = "NAME"
            }
        )
    }
}
Set-JiraIssue @parameters -Issue TEST-001, TEST-002
